From 72cf6252abf9ddc18ad557791fed6d3134350a42 Mon Sep 17 00:00:00 2001
From: Francois Romieu <romieu@fr.zoreil.com>
Date: Mon, 4 Jun 2007 21:53:37 +0200
Subject: [PATCH] forcedeth: power management fixes

Extracted from Ayaz Abdulla's message on netdev the 21 of may 2007
(see "[PATCH 2/4] forcedeth: fix power management support").
---
 drivers/net/forcedeth.c |   58 ++++++++++++++++++++++++++++++++++++----------
 1 files changed, 45 insertions(+), 13 deletions(-)

diff --git a/drivers/net/forcedeth.c b/drivers/net/forcedeth.c
index 4154fd0..393a497 100644
--- a/drivers/net/forcedeth.c
+++ b/drivers/net/forcedeth.c
@@ -794,6 +794,10 @@ struct fe_priv {

 	/* flow control */
 	u32 pause_flags;
+
+	/* power saved state */
+	u32 saved_config_space[64];
+	u32 saved_phyinterface;
 };

 /*
@@ -5264,39 +5268,67 @@ static int nv_suspend(struct pci_dev *pdev, pm_message_t state)
 {
 	struct net_device *dev = pci_get_drvdata(pdev);
 	struct fe_priv *np = netdev_priv(dev);
+	u8 __iomem *base = get_hwbase(dev);

-	if (!netif_running(dev))
-		goto out;
-
-	netif_device_detach(dev);
+	if (netif_running(dev)) {
+		netif_device_detach(dev);

-	// Gross.
-	nv_close(dev);
+		/* bring down the adapter */
+		nv_close(dev);
+	}

+	/* save any device state */
+	np->saved_phyinterface = readl(base + NvRegPhyInterface);
 	pci_save_state(pdev);
+
 	pci_enable_wake(pdev, pci_choose_state(pdev, state), np->wolenabled);
 	pci_set_power_state(pdev, pci_choose_state(pdev, state));
-out:
+
 	return 0;
 }

 static int nv_resume(struct pci_dev *pdev)
 {
 	struct net_device *dev = pci_get_drvdata(pdev);
+	struct fe_priv *np = netdev_priv(dev);
+	u8 __iomem *base = get_hwbase(dev);
 	int rc = 0;
+	u32 txreg;

-	if (!netif_running(dev))
-		goto out;
+	rc = pci_set_power_state(pdev, PCI_D0);
+	if (rc < 0)
+		goto out_disable;

-	netif_device_attach(dev);
+	rc = pci_restore_state(pdev);
+	if (rc < 0)
+		goto out_disable;

-	pci_set_power_state(pdev, PCI_D0);
-	pci_restore_state(pdev);
 	pci_enable_wake(pdev, PCI_D0, 0);

-	rc = nv_open(dev);
+	/* restore saved driver state */
+	txreg = readl(base + NvRegTransmitPoll);
+	txreg |= NVREG_TRANSMITPOLL_MAC_ADDR_REV;
+	writel(txreg, base + NvRegTransmitPoll);
+	writel(np->saved_phyinterface, base + NvRegPhyInterface);
+	writel(np->orig_mac[0], base + NvRegMacAddrA);
+	writel(np->orig_mac[1], base + NvRegMacAddrB);
+
+	/* re-initialize the phy */
+	phy_init(dev);
+
+	if (netif_running(dev)) {
+		netif_device_attach(dev);
+
+		/* bring up the adapter */
+		rc = nv_open(dev);
+	}
 out:
 	return rc;
+
+out_disable:
+	pci_disable_device(pdev);
+	dev_err(&pdev->dev, "resume failed (%d)\n", rc);
+	goto out;
 }
 #else
 #define nv_suspend NULL
-- 
