 }

 /**
@@ -295,7 +295,7 @@ static void hotplug_dock_devices(struct 
 {
 	struct dock_dependent_device *dd;

-	spin_lock(&ds->hp_lock);
+	mutex_lock(&ds->hp_lock);

 	/*
 	 * First call driver specific hotplug functions
