 	}
 }

+#define CONFIG_REGISTER 0x108
+static void __init calgary_disable_sc_timeout(void __iomem *bbar)
+{
+	u64 val64;
+	void __iomem *target;
+
+	target = calgary_reg(bbar, CONFIG_REGISTER);
+	val64 = be64_to_cpu(readq(target));
+	/* sets PHB1 timeout to 0x1110, which is a 2s timeout */
+	val64 &= 0xFFFFFF0FFFFFFFFF;
+	val64 |= 0x000000E000000000;
+	writeq(cpu_to_be64(val64), target);
+	readq(target); /* flush */
+}
