+static void __init calgary_increase_split_completion_timeout(void __iomem *bbar,
+	unsigned char busnum)
+{
+	u64 val64;
+	void __iomem *target;
+	unsigned long phb_shift = -1;
+	u64 mask;
+
+	switch (busno_to_phbid(busnum)) {
+	case 0: phb_shift = (63 - 19);
+		break;
+	case 1: phb_shift = (63 - 23);
+		break;
+	case 2: phb_shift = (63 - 27);
+		break;
+	case 3: phb_shift = (63 - 35);
+		break;
+	default:
+		BUG_ON(busno_to_phbid(busnum));
+	}
+
+	target = calgary_reg(bbar, CALGARY_CONFIG_REG);
+	val64 = be64_to_cpu(readq(target));
+
+	/* zero out this PHB's timer bits */
+	mask = ~(0xFUL << phb_shift);
+	printk("Calgary mask: 0x%Lx [%s]\n", mask,
+	       (mask == 0xFFFFFF0FFFFFFFFF) ? "same" : "different");
+	val64 &= mask;
+
+	val64 |= (CCR_2SEC_TIMEOUT << phb_shift);
+	printk("Calgary timeout val: 0x%lx [%s]\n",
+	       (CCR_2SEC_TIMEOUT << phb_shift),
+	       ((CCR_2SEC_TIMEOUT << phb_shift) == 0x000000E000000000) ?
+	       "same" : "different");
+	writeq(cpu_to_be64(val64), target);
+	readq(target); /* flush */
+}
+
 static void __init calgary_enable_translation(struct pci_dev *dev)
 {
